Service.create!(name: 'Life Purpose Reading', description: 'One session includes one hour of very gentle and light touch acupuncture with a short full body light massage to finish. Depending on the patient’s diagnosis, there may be herbal consultation and prescription to treat the condition and to help further heal the patient.', service_type: 'In-person', price: 25, duration: 30, active: true, practitioner_specialty_id: 5)
Service.create!(name: 'Holistic Nutrition & Meal Prepping', description: 'A Remote session can be done in the comfort of your own home or wherever you happen to be. A brief phone conversation between receiver and therapist will begin the session, followed by a 55 min period of relaxation/rest by the receiver. During this time, the therapist connects on an energetic level with the receiver and the session takes place.', service_type: 'Virtual', price: 30, duration: 75, active: true, practitioner_specialty_id: 6)
